import React, { useEffect, useState } from "react";
// import axios from "axios";
import Button from "react-bootstrap/Button";
import Accordion from "react-bootstrap/Accordion";
import Modal from "react-bootstrap/Modal";
import ListGroup from "react-bootstrap/ListGroup";

const Moreslok = () => {
  const [chapters, setChapters] = useState([]);
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(() => {
    const fetchChapters = async () => {
      try {
        const response = await axios.get("https://bhagavadgitaapi.in/chapters");
        console.log("Current Chapter response", response.data);
        setChapters(response.data);
      } catch (error) {
        console.error("Error", error);
      }
    };

    fetchChapters();
  }, []);

  return (
    <div>
      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header>
          <Modal.Title className="text-center">
            All Shlok From Chapter {chapters.chapter_number}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <ListGroup></ListGroup>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
      <div className="card border-warning">
        <div className="card-body">
          <h5 className="card-title"></h5>
          <p className="card-text">
            <Accordion>
              <Accordion.Item eventKey="0">
                <Accordion.Header>Hindi Summary</Accordion.Header>
                <Accordion.Body>
                  <b>{chapters.summary?.hi}</b>
                </Accordion.Body>
              </Accordion.Item>
              <Accordion.Item eventKey="1">
                <Accordion.Header>English Summary</Accordion.Header>
                <Accordion.Body>
                  <b>{chapters.summary?.en}</b>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
            <br />
          </p>
          <p className="card-text text-center">
            <button className="btn btn-outline-danger " onClick={handleShow}>
              Read Sholks
            </button>
          </p>
        </div>
      </div>
      <br />
    </div>
  );
};

export default Moreslok;
