import React, { useState, useRef } from 'react';
import audio from "../img/aathmaraama.mp3";

const Music = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  const togglePlay = () => {
    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    setIsPlaying(!isPlaying);
  };

  return (
    <div>
      <audio ref={audioRef} src={audio} />
      <button style={{padding:""}} onClick={togglePlay}>{isPlaying ?<i class="fa-solid fa-pause"></i>  : <i class="fa-solid fa-play"></i>}</button>


      <button onClick={togglePlay} className="btn btn-outline-success">
      <i className="fas fa-play" /> Play | Pause Audio
      </button>
    </div>
  );
};

export default Music;
