import axios from "axios";
import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const Chapters = () => {
    const [chapters, setChapters] = useState([]);

    useEffect(() => {
        const fetchChapters = async () => {
            try {
                const response = await axios.get("https://bhagavadgitaapi.in/chapters");
                console.log("Current Chapter response", response.data);
                setChapters(response.data);
            } catch (error) {
                console.error("Error", error);
            }
        };

        fetchChapters();
    }, []);

    return (
        <div className="container">
            <h1 className="my-4">Bhagavad Gita ChapterWise</h1>
            <div className="row">
                {chapters.map((chapter) => (
                    <div key={chapter.chapter_number} className="">
                        <div className="card h-100">
                            <div className="card-body">
                                <h2 className="card-title">{chapter.name}</h2>
                                <p className="card-text">Chapter Number: {chapter.chapter_number}</p>
                                <p className="card-text">Verses Count: {chapter.verses_count}</p>
                                <p className="card-text">Translation: {chapter.translation}</p>
                                <p className="card-text">English Transliteration: {chapter.summary.en}</p>
                                <p className="card-text">Hindi Transliteration: {chapter.summary.hi}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Mores;
