import React from 'react';
import { useSpeechSynthesis } from 'react-speech-kit';

const Demo = () => {
  const { speak } = useSpeechSynthesis();

  const speakText = (text) => {
    speak({ text });
  };

  return (
    <div>
      <button onClick={() => speakText('Hello, how are you?')}>
        Speak English
      </button>
      <button onClick={() => speakText('नमस्ते, आप कैसे हैं?')}>
        Speak Hindi
      </button>
    </div>
  );
};

export default Demo;
