import React, { useEffect, useState } from "react";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Accordion from "react-bootstrap/Accordion";
import Modal from "react-bootstrap/Modal";
import ListGroup from "react-bootstrap/ListGroup";

const Slokcard = () => {
  const [chapters, setChapters] = useState([]);
  const [show, setShow] = useState(false);
  const [selectedChapter, setSelectedChapter] = useState(null);
  const [shlokas, setShlokas] = useState([]);

  const handleClose = () => setShow(false);
  const handleShow = (chapter) => {
    setSelectedChapter(chapter);
    fetchShlokas(chapter.chapter_number);
    setShow(true);
  };

  useEffect(() => {
    const fetchChapters = async () => {
      try {
        const response = await axios.get("https://bhagavadgitaapi.in/chapters");
        setChapters(response.data);
        console.log("firstdata is" +chapters)
      } catch (error) {
        console.error("Error fetching chapters:", error);
      }
    };

    fetchChapters();
  }, []);

  const fetchShlokas = async (chapterNumber) => {
    try {
      const response = await axios.get(`https://bhagavadgitaapi.in/slok/${chapterNumber}/2`);
      setShlokas(response.data.shlokas);
      console.log("this second response"+shlokas);
    } catch (error) {
      console.error("Error fetching shlokas:", error);
    }
  };

  return (
    <div>
      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header>
          <Modal.Title className="text-center">
            All Shlok From Chapter {selectedChapter?.chapter_number}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <ListGroup>
            {shlokas.map((shloka, index) => (
              <ListGroup.Item key={index}>
                {shloka.verse_number}: {shloka.sanskrit} - {shloka.meaning.en}
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          {/* You can add functionality to save changes if needed */}
          {/* <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button> */}
        </Modal.Footer>
      </Modal>

      {chapters.map((chapter) => (
        <div key={chapter.chapter_number} className="card border-warning">
          <div className="card-body">
            <h5 className="card-title">
              {chapter.chapter_number}. {chapter.name}
            </h5>
            <h5 className="card-title">
              {chapter.meaning.en} / {chapter.meaning.hi}
            </h5>
            <p className="card-text">
              <Accordion>
                <Accordion.Item eventKey="0">
                  <Accordion.Header>Hindi Summary</Accordion.Header>
                  <Accordion.Body>
                    <b>{chapter.summary?.hi}</b>
                  </Accordion.Body>
                </Accordion.Item>
                <Accordion.Item eventKey="1">
                  <Accordion.Header>English Summary</Accordion.Header>
                  <Accordion.Body>
                    <b>{chapter.summary?.en}</b>
                  </Accordion.Body>
                </Accordion.Item>
              </Accordion>
            </p>
            <p className="card-text text-center">
              <button className="btn btn-outline-danger" onClick={() => handleShow(chapter)}>
                Read Shlokas
              </button>
            </p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Slokcard;
