import React, { useEffect, useState } from "react";
import axios from "axios";
import "../css/Slokcard.css";

const Slokcard = () => {
  const [chapters, setChapters] = useState([]);
  const [selectedChapter, setSelectedChapter] = useState(null);

  useEffect(() => {
    const fetchChapters = async () => {
      try {
        const response = await axios.get("https://bhagavadgitaapi.in/chapters");
        setChapters(response.data);
      } catch (error) {
        console.error("Error", error);
      }
    };

    fetchChapters();
  }, []);

  const handleChapterSelect = (chapter) => {
    setSelectedChapter(chapter);
  };

  return (
    <div className="slok-card-container">
      {chapters.map((chapter, index) => (
        <div key={index} className="slok-card">
          <h2>{chapter.name}</h2>
          <p>Chapter Number: {chapter.chapter_number}</p>
          <div className="dropdown">
            <button
              className="btn btn-primary dropdown-toggle"
              type="button"
              id={`dropdownMenuButton-${index}`}
              data-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false"
              onClick={() => handleChapterSelect(chapter)}
            >
              View Details
            </button>
            <div
              className={`dropdown-menu ${selectedChapter === chapter ? 'show' : ''}`}
              aria-labelledby={`dropdownMenuButton-${index}`}
            >
              <p className="dropdown-item">Verses Count: {chapter.verses_count}</p>
              <p className="dropdown-item">Summary (English): {chapter.summary.en}</p>
              <p className="dropdown-item">Summary (Hindi): {chapter.summary.hi}</p>
              {/* Add more details here if needed */}
            </div>
          </div>
          <hr />
        </div>
      ))}
    </div>
  );
};

export default Slokcard;
