import React, { useEffect, useState } from "react";
import axios from "axios";

const Slokcard = () => {
  const [chapters, setChapters] = useState([]);
  const [selectedChapter, setSelectedChapter] = useState(null);
  const [slokData, setSlokData] = useState(null);

  useEffect(() => {
    const fetchChapters = async () => {
      try {
        const response = await axios.get("https://bhagavadgitaapi.in/chapters");
        setChapters(response.data);
        console.log("Our 1st dta"+chapters)
      } catch (error) {
        console.error("Error", error);
      }
    };

    fetchChapters();
  }, []);

  const handleReadMore = async (chapterNumber) => {
    try {
      const response = await axios.get(`https://bhagavadgitaapi.in/slok/${chapterNumber}/2/`);
      setSlokData(response.data);
      console.log("Our 1st dta"+chapters)
    } catch (error) {
      console.error("Error", error);
    }
  };

  return (
    <div>
      <div className="slok-card-container">
        {chapters.map((chapter, index) => (
          <div key={index} className="slok-card">
            <h2>{chapter.name}</h2>
            <p>Chapter Number: {chapter.chapter_number}</p>
            <p>Verses Count: {chapter.verses_count}</p>
            <p>Summary: {chapter.summary.en}</p>
            <p>Summary: {chapter.summary.hi}</p>
            <button onClick={() => handleReadMore(chapter.chapter_number)}>Read More</button>
            <hr />
          </div>
        ))}
      </div>
      {slokData && (
        <div className="slok-details">
          <h3>Slok Details</h3>
          <p>{slokData.meaning}</p>
          {/* Add more details here if needed */}
        </div>
      )}
    </div>
  );
};

export default Slokcard;
